mapper.sql:
SELECT POINT_NO, user_no, point_status, payment_no, point_date, part_point, SUM(PART_POINT) OVER(ORDER BY POINT_NO) total_point, point_cancel_flag FROM POINTT
USER_NO = #{userNo}
ORDER BY POINT_NO DESC

mapper.select:

	SELECT SUM(PART_POINT)
	FROM POINTT
	WHERE USER_NO = #{userNo:NUMERIC}


	SELECT * 
	FROM (	SELECT inner_table.*,  ROWNUM AS row_seq 
			FROM (
				
												) inner_table 
			
			ROWNUM <= #{endRowNum} 
			 )
	
	row_seq BETWEEN #{startRowNum} AND #{endRowNum}
